mapper.select:
SELECT A.RNUM , A.ARTICLE_NO , A.TITLE , A.CONTENT , A.UPLOAD_DATE , A.READ_COUNT , A.MEM_CODE , A.NICKNAME FROM (SELECT ROWNUM RNUM , B.ARTICLE_NO , B.TITLE , B.CONTENT , B.UPLOAD_DATE , B.READ_COUNT , B.MEM_CODE , B.NICKNAME FROM (SELECT C.ARTICLE_NO , C.TITLE , C.CONTENT , C.UPLOAD_DATE , C.READ_COUNT , C.MEM_CODE , E.NICKNAME FROM COMMUNITY C JOIN MEMBER E ON(C.MEM_CODE = E.MEM_CODE) ORDER BY C.ARTICLE_NO DESC) B
WHERE ROWNUM <= #{ endRow }
) A
A.RNUM >= #{ startRow }
AND A.TITLE LIKE '%' || #{searchValue} || '%'
AND A.NICKNAME LIKE '%' || #{searchValue} || '%'

mapper.update:
UPDATE
    COMMUNITY A
SET   A.TITLE = #{ communityTitle }
  , A.CONTENT = #{ communityContent }
WHERE A.ARTICLE_NO = #{ communityNo }

mapper.delete:
DELETE
FROM   COMMUNITY A
WHERE  A.ARTICLE_NO = #{ no }
