mapper.insert:
INSERT
INTO Community A
(
     A.Community_NO
   , A.Community_TITLE
   , A.Community_CONTENT
)
VALUES
(
       SEQ_Community_NO.NEXTVAL
     , #{ CommunityTitle }
     , #{ CommunityContent }
)

mapper.update:
UPDATE
      Community A
SET   A.Community_TITLE = #{ CommunityTitle }
    , A.Community_CONTENT = #{ CommunityContent }
WHERE A.Community_NO = #{ no }

mapper.delete:
DELETE
FROM   Community A
WHERE  A.Community_NO = #{ no }

mapper.update:
UPDATE
       Community A
SET A.READ_COUNT = (SELECT B.READ_COUNT
                      FROM  Community B
                      WHERE B.Community_NO = #{ no }
                     ) + 1
WHERE A.Community_NO = #{ no }
