mapper.insert:
INSERT
INTO TBL_NOTICE A
    (
  A.NOTICE_NO
, A.NOTICE_TITLE
, A.NOTICE_BODY
, A.NOTICE_WRITER_MEMBER_NO
)
VALUES
    (
    SEQ_NOTICE_NO.NEXTVAL
        , #{ title }
        , #{ body }
        , #{ writerMemberNo }
    )

mapper.update:
UPDATE
    TBL_NOTICE A
SET A.NOTICE_COUNT = (SELECT B.NOTICE_COUNT
                      FROM TBL_NOTICE B
                      WHERE B.NOTICE_NO = #{ no }
                     ) + 1
WHERE A.NOTICE_NO = #{ no }

UPDATE
    NOTICE A
SET A.NOTICE_TITLE = #{ noticeTitle }
  , A.NOTICE_CONTENT = #{ noticeContent }
WHERE A.NOTICE_NO = #{ no }

mapper.delete:
DELETE
FROM TBL_NOTICE A
WHERE A.NOTICE_NO = #{ no }
