mapper.update:
UPDATE
      NOTICE A
SET   A.NOTICE_TITLE = #{ noticeTitle }
    , A.NOTICE_CONTENT = #{ noticeContent }
WHERE A.NOTICE_NO = #{ no }

mapper.delete:
DELETE
FROM   NOTICE A
WHERE  A.NOTICE_NO = #{ no }

mapper.update:
UPDATE
       NOTICE A
SET A.READ_COUNT = (SELECT B.READ_COUNT
                      FROM  NOTICE B
                      WHERE B.NOTICE_NO = #{ no }
                     ) + 1
WHERE A.NOTICE_NO = #{ no }
